import React, {useState} from 'react';
import Header from './Header';
import { Link, navigate } from '@reach/router';
import { Button, Form } from 'semantic-ui-react';
import axios from 'axios';
// import 'semantic-ui-css/semantic.min.css'

const PlayerForm = (props) => {

    const {initialName, initialPosition, onSubmitProp} = props;

    const [ name, setName ] = useState(initialName);
    const [ position, setPosition ] = useState(initialPosition);

    const canBeSubmitted = () => {
        const {name} = this.state;
        return name.length > 2; 
    }

    const onSubmitHandler = (e) => {
        if (!this.canBeSubmitted()) {
        e.preventDefault();
        onSubmitProp({name, position});
    }}

    const isEnabled = this.canBeSubmitted();

    return (
        <div>
                <div className="contentForm">
                <Form onSubmit={onSubmitHandler}>
                    <Form.Field>
                    <label>Player Name:</label>
                    <input type="text" onChange={(e) => setName(e.target.value)} value={name} />
                    </Form.Field>
                    <Form.Field>
                    <label>Preferred Position:</label>
                    <input type="text" onChange={(e) => setPosition(e.target.value)} value={position}  />
                    </Form.Field>
                    <Button disabled={!isEnabled} className="yellow large" style={{"marginTop": "10px"}} type='submit'>SUBMIT</Button>
                </Form>

                </div>
            </div>
    )
}

export default PlayerForm;
