import React, {useState} from 'react';
import Header from './Header';
import { Link, navigate } from '@reach/router';
import { Button, Form } from 'semantic-ui-react';
import axios from 'axios';
// import 'semantic-ui-css/semantic.min.css'

const PlayerForm = (props) => {

    const {onSubmitProp} = props;

    const [ name, setName ] = useState("");
    const [ position, setPosition ] = useState("");

    const onSubmitHandler = (e) => {
        e.preventDefault();
        onSubmitProp({name, position});
    }

    return (
        <div>
            <Header />
            <div className="listContent">
                <div className="contentHeader">
                    <h2><Link to="/players/list">List</Link></h2>
                    <div className="divider"></div>
                    <h2><Link to="/players/addplayer">Add Player</Link></h2>
                </div>
                <div className="contentForm">
                <Form onSubmit={onSubmitHandler}>
                    <h2 className="formTitle">Add Player</h2>
                    <Form.Field>
                    <label>Player Name:</label>
                    <input type="text" onChange={(e) => setName(e.target.value)} value={name} />
                    </Form.Field>
                    <Form.Field>
                    <label>Preferred Position:</label>
                    <input type="text" onChange={(e) => setPosition(e.target.value)} value={position}  />
                    </Form.Field>
                    <Button className="green" type='submit'>ADD</Button>
                </Form>

                </div>
            </div>
        </div>
    )
}

export default PlayerForm;
