import React, {useState, useEffect} from 'react';
import {Link} from "@reach/router";
import Header from '../components/Header';
import { Table, Button } from 'semantic-ui-react'
import axios from 'axios';

const Status = (props) => {
    const {id} = props;
    const [players, setPlayers] = useState([]);
    const [updatePlayer, setUpdatePlayer] = useState({});

    const [status, setStatus] = useState();
    const [loaded, setLoaded] = useState(false);
    const [playing, setPlaying] = useState(false);
    const [notPlaying, setNotPlaying] = useState(false);
    const [undecided, setUndecided] = useState(false);

    // to get the player list
    useEffect(() => {
        axios.get("http://localhost:8010/api/players")
            .then(res => {
                setPlayers(res.data);
                setStatus(res.data);
            })
            .catch(err => console.log(err))
    }, []);

    // to update the status
    const updateStatus = (e, id, playing, notPlaying, undecided) => {
        e.preventDefault();
        let currentStatus = status;
        if (playing) {
            currentStatus.playing = !currentStatus.playing;
        } else if (notPlaying){
            currentStatus.notPlaying = !currentStatus.notPlaying;
        } else if (undecided){
            currentStatus.undecided = !currentStatus.undecided;
        } console.log(currentStatus);
        axios.get(`http://localhost:8010/api/players/${id}`)
            .then(res => {
                setUpdatePlayer(res.data);
                console.log(updatePlayer);
            })

        axios.put(`http://localhost:8010/api/players/${id}`, updatePlayer) 
            .then(res => {
                setStatus(res.data);
                // setLoaded(true);
            })
            .catch(err => console.log(err));
        }
    return (
        <div>
            <Header />
            <div className="listContent">
                <h1 className="statusTitle">Player Status - Game 1</h1>
                <div className="contentHeader">
                    {/* <h2><Link to="/players/addplayer">Add Player</Link></h2>
                    <div className="divider"></div>
                    <h2><Link to="/players/list">Lineup</Link></h2> */}
                    <h2><Link to="/">Game 1</Link></h2>
                    <div className="divider"></div>
                    <h2><Link to="/">Game 2</Link></h2>
                    <div className="divider"></div>
                    <h2><Link to="/">Game 3</Link></h2>
                </div>
                <div className="table">
                <Table className="ui yellow table">
                    <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Player Name</Table.HeaderCell>
                        <Table.HeaderCell>Actions</Table.HeaderCell>
                    </Table.Row>
                    </Table.Header>
            {/* {loaded && ( */}
                    <Table.Body>
                        {players.map((value, idx) => {
                        return <Table.Row key={idx}>
                        <Table.Cell>
                            <Link to={`/players/${value._id}`}>{value.name}</Link>
                        </Table.Cell>
                        <Table.Cell>
                            {/* <Button className="red" content='Playing' /> */}
                            <Button style={{"marginRight": "50px"}} 
                            className={ value.playing === true ? "green" : ""}
                            onClick={(e) => updateStatus(value._id, true, false, false)}
                            content='Playing' />
                            <Button style={{"marginRight": "50px"}} 
                            className={ value.notPlaying === true ? "red" : ""}
                            onClick={(e) => updateStatus(value._id, false, true, false)}                        
                            content='Not Playing' />
                            <Button 
                            className={ value.undecided === true ? "yellow" : ""}
                            onClick={(e) => updateStatus(value._id, false, false, true)}                                  
                            content='Undecided' />
                        </Table.Cell>
                        </Table.Row>
                        })}
                    </Table.Body>
                {/* )} */}
                </Table>
                </div>
         </div>
        </div>
    )
}

export default Status;
